INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('11700', 'Vorsteuer 8.1% auf Mat. + DL', 'A', 'A', 'AP_tax', '117', false, true, '', 'Input VAT (Vorsteuer) at 8.1% on materials and services. Use this tax code when booking purchase invoices for raw materials, trading goods, supplies, and third-party services where the standard VAT rate of 8.1% applies. This is the most common input VAT account for operational purchases.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('11710', 'Zoll auf Mat. + DL', 'A', 'A', 'AP_tax', '117', false, true, 'zollkonto1', 'Customs duty (Zoll) on imported materials and services. Use when booking customs/import duty charges on materials and services imported into Switzerland. Applies to customs declarations and import duty invoices.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('11711', 'Zoll auf Inv. + übr. BA', 'A', 'A', 'AP_tax', '117', false, true, 'zollkonto2', 'Customs duty (Zoll) on imported investments and other operating expenses. Use when booking customs/import duty charges on imported capital goods (machines, equipment, vehicles) and other operating expense items.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('11720', 'Vorsteuer 2.6% auf Mat. + DL', 'A', 'A', 'AP_tax', '117', false, true, '', 'Input VAT (Vorsteuer) at 2.6% on materials and services. Use this tax code when booking purchase invoices for materials and services subject to the reduced VAT rate of 2.6% (e.g. food, beverages, books, newspapers, medications, agricultural supplies).');
