INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('2200', 'Geschuldete MWST', 'A', 'L', 'AP_amount', '220', false, true, '', 'VAT payable – settlement account. This is the main VAT clearing/settlement account used for reconciling the periodic VAT return (MWST-Abrechnung). Nets input VAT against output VAT to determine amount owed to or receivable from the Swiss Federal Tax Administration (ESTV).');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22010', 'MWST 8.1%', 'A', 'L', 'AR_tax:IC_taxpart:IC_taxservice', '220', false, true, '', 'Output VAT (MWST) at 8.1% – standard rate. Use this tax code when booking sales invoices and revenue subject to the standard Swiss VAT rate of 8.1%. Applies to most goods and services sold domestically.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22020', 'MWST 3.8%', 'A', 'L', 'AR_tax:IC_taxpart:IC_taxservice', '220', false, true, '', 'Output VAT (MWST) at 3.8% – special rate for accommodation. Use this tax code when booking sales invoices for accommodation/lodging services subject to the special VAT rate of 3.8%.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22030', 'MWST 2.6%', 'A', 'L', 'AR_tax:IC_taxpart:IC_taxservice', '220', false, true, '', 'Output VAT (MWST) at 2.6% – reduced rate. Use this tax code when booking sales invoices for goods and services subject to the reduced VAT rate of 2.6% (e.g. food, beverages, books, newspapers, medications).');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22040', 'Bezugsteuer 8.1%', 'A', 'L', 'AP_tax', '220', false, true, '', 'Reverse charge VAT (Bezugsteuer) at 8.1% – liability side. Use when self-assessing VAT on services purchased from abroad. This is the output/liability counterpart to account 11760. Both accounts are used together for reverse charge bookings.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22050', 'Befreite Leistungen, Exporte MWST 0% (Ziff. 220)', 'A', 'L', '', '220', false, true, '', 'VAT-exempt supplies and exports at 0% (MWST declaration line 220). Use this tax code for genuinely VAT-exempt sales and exports. Examples: exported goods, services to foreign recipients where exemption applies. Revenue is reported but no VAT is charged.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22051', 'Leistungen im Ausland MWST 0% (Ziff. 221)', 'A', 'L', '', '220', false, true, '', 'Services performed abroad at 0% (MWST declaration line 221). Use this tax code for services where the place of supply is outside Switzerland. No Swiss VAT applies. Examples: consulting for foreign clients where place of supply rules shift VAT abroad.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22052', 'Nicht steuerbare Leistungen MWST 0% (Ziff. 230)', 'A', 'L', '', '220', false, true, '', 'Non-taxable services at 0% (MWST declaration line 230). Use this tax code for services explicitly excluded from Swiss VAT. Examples: certain financial services, insurance services, healthcare, education, and cultural services exempt under MWSTG.');
