INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22054', 'Spenden, Dividenden, Schadenersatz MWST 0% (Ziff. 910)', 'A', 'L', '', '220', false, true, '', 'Donations, dividends, and compensation payments at 0% (MWST declaration line 910). Use this tax code for received donations, dividend income, and damage compensation payments that are not subject to VAT but must be reported in the VAT return.');
INSERT INTO chart (accno, description, charttype, category, link, gifi_accno, contra, allow_gl, symbol_link, detail) VALUES ('22055', 'Nichtentgelt MWST 0%', 'A', 'L', '', '220', false, true, '', 'Non-consideration amounts at 0% (Nichtentgelt). Use this tax code for transactions without consideration that are not subject to VAT, such as certain internal transfers or non-commercial transactions.');
